import React, { useState } from 'react';
import './EmploymentDetails.css';
import FetchEmployment from './fetchEmployment';

const EmploymentDetails = () => {
  const [activeTab, setActiveTab] = useState('');
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [companyName, setCompanyName] = useState('');
  const [selectedIncomeRange, setSelectedIncomeRange] = useState('');

  const tabs = [
    'Full-time employed',
    'Part-time employed',
    'Self-employed',
    'Retired',
    'Student',
    'Not in employment'
  ];

  const openTab = (tabName) => setActiveTab(tabName);

  const handleIndustrySelect = (industry) => {
    setSelectedIndustry(industry);
  };

  const handleCompanyNameChange = (event) => {
    setCompanyName(event.target.value);
  };

  const handleIncomeRangeSelect = (range) => {
    setSelectedIncomeRange(range);
  };

  return (
    <div className="container">
      <div className="title-line">
        <h3 className="number">07</h3>
        <h3 className="details">Employment Details</h3>
      </div>
      <hr />
      <h3>What's your employment status?</h3>
      <p>We ask this in order to improve security on your account.</p>
      <div className="tab">
        {tabs.map((tab, index) => (
          <button
            key={index}
            className={`tablinks ${activeTab === tab ? 'active' : ''}`}
            onClick={() => openTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className={`tabcontent ${activeTab === 'Full-time employed' ? 'active' : ''}`}>
        <h3>Full-time employed</h3>
        <p>Full-time employed is the capital city of England.</p>
        <h3>What industry do you work in?</h3>
        <p>Please select the closest option to your industry.</p>
        <div className="dropdown">
          <button className={`dropbtn ${selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {selectedIndustry || 'Select your industry'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Agriculture')}>Agriculture</button>
            <button onClick={() => handleIndustrySelect('Manufacturing')}>Manufacturing</button>
            {/* Add more industry options as needed */}
          </div>
        </div>
        <h3>What's name of your company?</h3>
        <input
          type="text"
          placeholder="Enter your company name"
          value={companyName}
          onChange={handleCompanyNameChange}
        />
        <h3>What's your annual income?</h3>
        <p>This includes your salary, benefits, investments, or other income you may receive.</p>
        <div className="annual-income-options">
          <button
            className={`tablinks ${selectedIncomeRange === '£0-£24,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£0-£24,999')}
          >
            £0-£24,999
          </button>
          <button
            className={`tablinks ${selectedIncomeRange === '£25,000-£49,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£25,000-£49,999')}
          >
            £25,000-£49,999
          </button>
          <button
            className={`tablinks ${selectedIncomeRange === '£50,000-£99,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£50,000-£99,999')}
          >
            £50,000-£99,999
          </button>
          <button
            className={`tablinks ${selectedIncomeRange === '£100,000-£149,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£100,000-£149,999')}
          >
            £100,000-£149,999
          </button>
          <button
            className={`tablinks ${selectedIncomeRange === '£150,000+' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£150,000+')}
          >
            £150,000+
          </button>
        </div>
      </div>

      <div className={`tabcontent ${activeTab === 'Part-time employed' ? 'active' : ''}`}>
        <h3>Part-time employed</h3>
        <p>Part-time employed is the capital of France.</p>
        <h3>What industry do you work in?</h3>
        <p>Please select the closest option to your industry.</p>
        <div className="dropdown">
          <button className={`dropbtn ${selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {selectedIndustry || 'Select your industry'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Agriculture')}>Agriculture</button>
            <button onClick={() => handleIndustrySelect('Manufacturing')}>Manufacturing</button>
            {/* Add more industry options as needed */}
          </div>
        </div>
        <h3>What's name of your company?</h3>
        <input
          type="text"
          placeholder="Enter your company name"
          value={companyName}
          onChange={handleCompanyNameChange}
        />
        <h3>What's your annual income?</h3>
        <p>This includes your salary, benefits, investments, or other income you may receive.</p>
        <div className="annual-income-options">
          <button
            className={`tablinks ${selectedIncomeRange === '£0-£24,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£0-£24,999')}
          >
            £0-£24,999
          </button>
          <button
            className={`tablinks ${selectedIncomeRange === '£25,000-£49,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£25,000-£49,999')}
          >
            £25,000-£49,999
          </button>
          <button
            className={`tablinks ${selectedIncomeRange === '£50,000-£99,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£50,000-£99,999')}
          >
            £50,000-£99,999
          </button>
          <button
            className={`tablinks ${selectedIncomeRange === '£100,000-£149,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£100,000-£149,999')}
          >
            £100,000-£149,999
          </button>
          <button
            className={`tablinks ${selectedIncomeRange === '£150,000+' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£150,000+')}
          >
            £150,000+
          </button>
        </div>
      </div>

      <div className={`tabcontent ${activeTab === 'Retired' ? 'active' : ''}`}>
        <h3>Retired</h3>
        <p>Retired is the capital of Japan.</p>
        <h3>What's your last occupation?</h3>
        <p>Please select the closest option to your last occupation.</p>
        <div className="dropdown">
          <button className={`dropbtn ${selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {selectedIndustry || 'Select your occupation'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Teacher')}>Teacher</button>
            <button onClick={() => handleIndustrySelect('Engineer')}>Engineer</button>
            {/* Add more occupation options as needed */}
          </div>
        </div>
        <h3>How long have you been retired?</h3>
        <input
          type="text"
          placeholder="Enter number of years retired"
          value={companyName}
          onChange={handleCompanyNameChange}
        />
      </div>

      <div className={`tabcontent ${activeTab === 'Student' ? 'active' : ''}`}>
        <h3>Student</h3>
        <p>Student is the capital of Japan.</p>
        <h3>What's your field of study?</h3>
        <p>Please select the closest option to your field of study.</p>
        <div className="dropdown">
          <button className={`dropbtn ${selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {selectedIndustry || 'Select your field of study'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Computer Science')}>Computer Science</button>
            <button onClick={() => handleIndustrySelect('Biology')}>Biology</button>
            {/* Add more field of study options as needed */}
          </div>
        </div>
        <h3>Name of your institution?</h3>
        <input
          type="text"
          placeholder="Enter your institution name"
          value={companyName}
          onChange={handleCompanyNameChange}
        />
      </div>

      <div className={`tabcontent ${activeTab === 'Not in employment' ? 'active' : ''}`}>
        <h3>Not in employment</h3>
        <p>Not in employment is the capital of Japan.</p>
        <h3>What was your last occupation?</h3>
        <p>Please select the closest option to your last occupation.</p>
        <div className="dropdown">
          <button className={`dropbtn ${selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {selectedIndustry || 'Select your last occupation'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Manager')}>Manager</button>
            <button onClick={() => handleIndustrySelect('Salesperson')}>Salesperson</button>
            {/* Add more occupation options as needed */}
          </div>
        </div>
        <h3>How long have you been unemployed?</h3>
        <input
          type="text"
          placeholder="Enter number of months unemployed"
          value={companyName}
          onChange={handleCompanyNameChange}
        />
      </div>
      
      <div className="button-container">
        <FetchEmployment />
      </div>
    </div>
  );
};

export default EmploymentDetails;
