import React, { useState } from 'react';
import './EmploymentDetails.css';
import FetchEmployment from './fetchEmployment';

const EmploymentDetails = () => {
  const [activeTab, setActiveTab] = useState('');
  const [fullTimeData, setFullTimeData] = useState({
    selectedIndustry: '',
    companyName: '',
    selectedIncomeRange: ''
  });
  const [partTimeData, setPartTimeData] = useState({
    selectedIndustry: '',
    companyName: '',
    selectedIncomeRange: ''
  });
  const [selfEmployedData, setSelfEmployedData] = useState({
    selectedIndustry: '',
    selectedIncomeRange: ''
  });
  const [retiredData, setRetiredData] = useState({
    selectedIndustry: '',
    yearsRetired: ''
  });
  const [studentData, setStudentData] = useState({
    selectedIndustry: '',
    institutionName: ''
  });
  const [unemployedData, setUnemployedData] = useState({
    selectedIndustry: '',
    monthsUnemployed: ''
  });

  const tabs = [
    'Full-time employed',
    'Part-time employed',
    'Self-employed',
    'Retired',
    'Student',
    'Not in employment'
  ];

  const openTab = (tabName) => {
    setActiveTab(tabName);
    clearOtherTabs(tabName);
  };

  const clearOtherTabs = (currentTab) => {
    if (currentTab !== 'Full-time employed') setFullTimeData({ selectedIndustry: '', companyName: '', selectedIncomeRange: '' });
    if (currentTab !== 'Part-time employed') setPartTimeData({ selectedIndustry: '', companyName: '', selectedIncomeRange: '' });
    if (currentTab !== 'Self-employed') setSelfEmployedData({ selectedIndustry: '', selectedIncomeRange: '' });
    if (currentTab !== 'Retired') setRetiredData({ selectedIndustry: '', yearsRetired: '' });
    if (currentTab !== 'Student') setStudentData({ selectedIndustry: '', institutionName: '' });
    if (currentTab !== 'Not in employment') setUnemployedData({ selectedIndustry: '', monthsUnemployed: '' });
  };

  const handleIndustrySelect = (industry, tabName) => {
    switch (tabName) {
      case 'Full-time employed':
        setFullTimeData({ ...fullTimeData, selectedIndustry: industry });
        break;
      case 'Part-time employed':
        setPartTimeData({ ...partTimeData, selectedIndustry: industry });
        break;
      case 'Self-employed':
        setSelfEmployedData({ ...selfEmployedData, selectedIndustry: industry });
        break;
      case 'Retired':
        setRetiredData({ ...retiredData, selectedIndustry: industry });
        break;
      case 'Student':
        setStudentData({ ...studentData, selectedIndustry: industry });
        break;
      case 'Not in employment':
        setUnemployedData({ ...unemployedData, selectedIndustry: industry });
        break;
      default:
        break;
    }
  };

  const handleCompanyNameChange = (event, tabName) => {
    switch (tabName) {
      case 'Full-time employed':
        setFullTimeData({ ...fullTimeData, companyName: event.target.value });
        break;
      case 'Part-time employed':
        setPartTimeData({ ...partTimeData, companyName: event.target.value });
        break;
      default:
        break;
    }
  };

  const handleIncomeRangeSelect = (range, tabName) => {
    switch (tabName) {
      case 'Full-time employed':
        setFullTimeData({ ...fullTimeData, selectedIncomeRange: range });
        break;
      case 'Part-time employed':
        setPartTimeData({ ...partTimeData, selectedIncomeRange: range });
        break;
      default:
        break;
    }
  };

  const handleYearsRetiredChange = (event) => {
    setRetiredData({ ...retiredData, yearsRetired: event.target.value });
  };

  const handleInstitutionNameChange = (event) => {
    setStudentData({ ...studentData, institutionName: event.target.value });
  };

  const handleMonthsUnemployedChange = (event) => {
    setUnemployedData({ ...unemployedData, monthsUnemployed: event.target.value });
  };

  return (
    <div className="container">
      <div className="title-line">
        <h3 className="number">07</h3>
        <h3 className="details">Employment Details</h3>
      </div>
      <hr />
      <h3>What's your employment status?</h3>
      <p>We ask this in order to improve security on your account.</p>
      <div className="tab">
        {tabs.map((tab, index) => (
          <button
            key={index}
            className={`tablinks ${activeTab === tab ? 'active' : ''}`}
            onClick={() => openTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className={`tabcontent ${activeTab === 'Full-time employed' ? 'active' : ''}`}>
        <h3>What industry do you work in?</h3>
        <p>Please select the closest option to your industry.</p>
        <div className="dropdown">
          <button className={`dropbtn ${fullTimeData.selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {fullTimeData.selectedIndustry || 'Select your industry'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Agriculture', 'Full-time employed')}>Agriculture</button>
            <button onClick={() => handleIndustrySelect('Manufacturing', 'Full-time employed')}>Manufacturing</button>
            {/* Add more industry options as needed */}
          </div>
        </div>
        <h3>What's name of your company?</h3>
        <input
          className='dropbtn'
          type="text"
          placeholder="Enter your company name"
          value={fullTimeData.companyName}
          onChange={(e) => handleCompanyNameChange(e, 'Full-time employed')}
        />
        <h3>What's your annual income?</h3>
        <p>This includes your salary, benefits, investments, or other income you may receive.</p>
        <div className="annual-income-options">
          <button
            className={`tablinks ${fullTimeData.selectedIncomeRange === '£0-£24,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£0-£24,999', 'Full-time employed')}
          >
            £0-£24,999
          </button>
          <button
            className={`tablinks ${fullTimeData.selectedIncomeRange === '£25,000-£49,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£25,000-£49,999', 'Full-time employed')}
          >
            £25,000-£49,999
          </button>
          <button
            className={`tablinks ${fullTimeData.selectedIncomeRange === '£50,000-£99,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£50,000-£99,999', 'Full-time employed')}
          >
            £50,000-£99,999
          </button>
          <button
            className={`tablinks ${fullTimeData.selectedIncomeRange === '£100,000-£149,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£100,000-£149,999', 'Full-time employed')}
          >
            £100,000-£149,999
          </button>
          <button
            className={`tablinks ${fullTimeData.selectedIncomeRange === '£150,000+' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£150,000+', 'Full-time employed')}
          >
            £150,000+
          </button>
        </div>
      </div>

      <div className={`tabcontent ${activeTab === 'Part-time employed' ? 'active' : ''}`}>
        <h3>What industry do you work in?</h3>
        <p>Please select the closest option to your industry.</p>
        <div className="dropdown">
          <button className={`dropbtn ${partTimeData.selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {partTimeData.selectedIndustry || 'Select your industry'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Agriculture', 'Part-time employed')}>Agriculture</button>
            <button onClick={() => handleIndustrySelect('Manufacturing', 'Part-time employed')}>Manufacturing</button>
            {/* Add more industry options as needed */}
          </div>
        </div>
        <h3>What's name of your company?</h3>
        <input
          className='dropbtn'
          type="text"
          placeholder="Enter your company name"
          value={partTimeData.companyName}
          onChange={(e) => handleCompanyNameChange(e, 'Part-time employed')}
        />
        <h3>What's your annual income?</h3>
        <p>This includes your salary, benefits, investments, or other income you may receive.</p>
        <div className="annual-income-options">
          <button
            className={`tablinks ${partTimeData.selectedIncomeRange === '£0-£24,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£0-£24,999', 'Part-time employed')}
          >
            £0-£24,999
          </button>
          <button
            className={`tablinks ${partTimeData.selectedIncomeRange === '£25,000-£49,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£25,000-£49,999', 'Part-time employed')}
          >
            £25,000-£49,999
          </button>
          <button
            className={`tablinks ${partTimeData.selectedIncomeRange === '£50,000-£99,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£50,000-£99,999', 'Part-time employed')}
          >
            £50,000-£99,999
          </button>
          <button
            className={`tablinks ${partTimeData.selectedIncomeRange === '£100,000-£149,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£100,000-£149,999', 'Part-time employed')}
          >
            £100,000-£149,999
          </button>
          <button
            className={`tablinks ${partTimeData.selectedIncomeRange === '£150,000+' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£150,000+', 'Part-time employed')}
          >
            £150,000+
          </button>
        </div>
      </div>

      <div className={`tabcontent ${activeTab === 'Self-employed' ? 'active' : ''}`}>
        <h3>What industry do you work in?</h3>
        <p>Please select the closest option to your industry.</p>
        <div className="dropdown">
          <button className={`dropbtn ${selfEmployedData.selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {selfEmployedData.selectedIndustry || 'Select your industry'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Agriculture', 'Self-employed')}>Agriculture</button>
            <button onClick={() => handleIndustrySelect('Manufacturing', 'Self-employed')}>Manufacturing</button>
            {/* Add more industry options as needed */}
          </div>
        </div>
        <h3>What's your annual income?</h3>
        <p>This includes your salary, benefits, investments, or other income you may receive.</p>
        <div className="annual-income-options">
          <button
            className={`tablinks ${selfEmployedData.selectedIncomeRange === '£0-£24,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£0-£24,999', 'Self-employed')}
          >
            £0-£24,999
          </button>
          <button
            className={`tablinks ${selfEmployedData.selectedIncomeRange === '£25,000-£49,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£25,000-£49,999', 'Self-employed')}
          >
            £25,000-£49,999
          </button>
          <button
            className={`tablinks ${selfEmployedData.selectedIncomeRange === '£50,000-£99,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£50,000-£99,999', 'Self-employed')}
          >
            £50,000-£99,999
          </button>
          <button
            className={`tablinks ${selfEmployedData.selectedIncomeRange === '£100,000-£149,999' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£100,000-£149,999', 'Self-employed')}
          >
            £100,000-£149,999
          </button>
          <button
            className={`tablinks ${selfEmployedData.selectedIncomeRange === '£150,000+' ? 'active' : ''}`}
            onClick={() => handleIncomeRangeSelect('£150,000+', 'Self-employed')}
          >
            £150,000+
          </button>
        </div>
      </div>

      <div className={`tabcontent ${activeTab === 'Retired' ? 'active' : ''}`}>
        <h3>What was your last occupation?</h3>
        <p>Please select the closest option to your last occupation.</p>
        <div className="dropdown">
          <button className={`dropbtn ${retiredData.selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {retiredData.selectedIndustry || 'Select your last occupation'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Manager', 'Retired')}>Manager</button>
            <button onClick={() => handleIndustrySelect('Salesperson', 'Retired')}>Salesperson</button>
            {/* Add more occupation options as needed */}
          </div>
        </div>
        <h3>How long have you been retired?</h3>
        <input
          className='dropbtn'
          type="number"
          placeholder="Enter number of years retired"
          value={retiredData.yearsRetired}
          onChange={handleYearsRetiredChange}
        />
      </div>

      <div className={`tabcontent ${activeTab === 'Student' ? 'active' : ''}`}>
        <h3>What's your field of study?</h3>
        <p>Please select the closest option to your field of study.</p>
        <div className="dropdown">
          <button className={`dropbtn ${studentData.selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {studentData.selectedIndustry || 'Select your field of study'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Computer Science', 'Student')}>Computer Science</button>
            <button onClick={() => handleIndustrySelect('Business Administration', 'Student')}>Business Administration</button>
            {/* Add more field of study options as needed */}
          </div>
        </div>
        <h3>Name of your institution?</h3>
        <input
          className='dropbtn'
          type="text"
          placeholder="Enter your institution name"
          value={studentData.institutionName}
          onChange={handleInstitutionNameChange}
        />
      </div>

      <div className={`tabcontent ${activeTab === 'Not in employment' ? 'active' : ''}`}>
        <h3>What was your last occupation?</h3>
        <p>Please select the closest option to your last occupation.</p>
        <div className="dropdown">
          <button className={`dropbtn ${unemployedData.selectedIndustry ? 'selected-option' : ''}`} onClick={() => setSelectedIndustry('')}>
            {unemployedData.selectedIndustry || 'Select your last occupation'}
            <span className="arrow">&#9662;</span>
          </button>
          <div className="dropdown-content">
            <button onClick={() => handleIndustrySelect('Manager', 'Not in employment')}>Manager</button>
            <button onClick={() => handleIndustrySelect('Salesperson', 'Not in employment')}>Salesperson</button>
            {/* Add more occupation options as needed */}
          </div>
        </div>
        <h3>How long have you been unemployed?</h3>
        <input
          className='dropbtn'
          type="text"
          placeholder="Enter number of months unemployed"
          value={unemployedData.monthsUnemployed}
          onChange={handleMonthsUnemployedChange}
        />
      </div>
      
      <div className="button-container">
        <FetchEmployment />
      </div>
    </div>
  );
};

export default EmploymentDetails;
